css:
#img{
    padding: 1px;
    position: Center;
    top: 0px; /* Adjust the top position as needed */
    left: 10px; /* Adjust the left position as needed */
    max-width: 150px;
    max-height:150px;
}

.container {
    width: 100%;
    max-width: 650px; /* Adjust the maximum width as needed */
    margin: 0 auto; /* Center the container horizontally */
    padding: 20px; /* Add some padding to the container */
    border: 1px solid #b39e9f; /* Add a border and match the color */
    background-color: #fff;
}

body {
    font-family: Arial, sans-serif;
    background-color: #f4f4f4;
    margin: 0;
    padding: 0;
}

/* Style the header section */
#header {

    position:center;
    background-color: #826a60;
    width: 100%;
    color:white;
    text-align: center;
    padding: 20px;
}

#name {
    font-size: 24px;
    margin: 0;
    color:white;
}

#email {
    font-size: 16px;
    margin: 0;
}

/* Style the left and right sections */
.left,
.right {
    display: inline-block;
    vertical-align: top;
    padding: 20px;
}

.right {
    width: 70%;
}

/* Style CV Highlights section */
h3 {

    position:center;
    width: 100%;
    background-color: #b39e9f;
    color: white;
    padding: 10px;
}

ul {
    list-style-type: disc;
    margin-left: 20px;
}

/* Style Professional Experience section */
#company-name {
    font-size: 18px;
    margin: 0;
}

#job-title {
    font-weight: bold;
}

/* Style Educational Qualifications table */
table {
    width: 100%;
    border-collapse: collapse;
}

tr {
    border-bottom: 1px solid #ddd;
}

td {
    padding: 8px;
}

#heading {
    background-color: #b39e9f;
    color: white;
}

/* Style Independent Courses section */
#course-name {
    font-weight: bold;
}

/* Style Technical Skills section */
#course-name {
    font-weight: bold;
}
.print-format th {
        color:black;
        font-weight:bold;
    }

a {
    color: #b39e9f;
    text-decoration: none;
}

a:hover {
    text-decoration: underline;
}
/* Define the styles for the rating */
.rating {
    width: 200px;
    height: 8px;
    position: relative;
    margin-top: 5px;
}

.rating-bar {
    width: 100%;
    height: 100%;
    background-color: #f3f2f2;
}

.rating-value {
    height: 100%;
    background-color: #b39e9f;
}
