css:
body {
            font-family: Arial, sans-serif;
            background-color: #f8f8f8; /* Lighter background color */
            margin: 0;
            padding: 0;
            color: #000000; /* Black font color */
        }

        /* Style the header section */
        #header {
            background-color: #bdd7e7; /* Lightest blue */
            color: #000000; /* Black font color */
            text-align: Right;
            padding: 30px;
            position: relative; /* Add this to position the image */
            border-radius: 1%;
        }

        #name {
            font-size: 52px;
            color: #000000; /* Black font color */
            margin: 0;
        }

        #email {
            font-size: 18px;
            margin: 0;
        }

        /* Style the candidate's image */
        #candidate-image {
            position: absolute;
            padding: 1px;
            top: 4px; /* Adjust the top position as needed */
            left: 20px; /* Adjust the left position as needed */
            max-width: 125px; /* Adjust the width as needed */
            max-height:125px;
            border-radius: 35%; /* Make the image round */

        }

        /* Style the main content container */
        #content {
            max-width: 800px;
            margin: 0 auto;
            background-color: #ffffff; /* White */
            padding: 20px;
        }

/* Style CV Highlights section */
h2 {
    background-color: #d2ebf3; /* Very light teal */
    color: #000000; /* Black font color */
    padding: 10px;
    margin: 20px 0 0 0;
}

ul {
    list-style-type: disc;
    margin-left: 20px;
}

/* Style Professional Experience section */
.job-entry {
    margin-bottom: 20px;
    font-size: 10px;
}

.job-title {
    font-size: 20px;
    font-weight: bold;
}
.header{
    gap:20px;
}
.company-name {
    font-size: 18px;
}

/* Style Educational Qualifications table */
table {
    width: 100%;
    border-collapse: collapse;
    margin-top: 0px;
}

tr {
    border-bottom: 1px solid #ddd;
}

th, td {
    padding: 8px;
}

th {
    background-color: #bdd7e7; /* Lightest blue */
    color: #000000; /* Black font color */
}

/* Style Technical Skills section */
#skills {
    margin-top: 20px;
}

/* Style Certifications / Awards section */
#awards {
    margin-top: 20px;
    font-size: 16px;
    color: #000000;
}

/* Style Personal Information section */
#personal-info {
    margin-top: 20px;
}
/* Style Other Information section */
#other-info {
    margin-top: 20px;
}

/* Style Declaration section */
#declaration {
    margin-top: 20px;
}

/* Style links */
a {
    color: #000000; /* Black font color */
    text-decoration: none;
}

a:hover {
    text-decoration: underline;
}


        /* Style links within the skills section to make them black */
        #skills  {
            font-size: 16px;
            color: #000000; /* Black font color */
        }
        #Project {
            margin-top: 0px;
        }
        /* CSS to style the stars */
.star-rating {
    display: flex;
    align-items: center;
}

.star-container {
    display: flex;
    align-items: center;
}

.star-container i {
    color: #bdd7e7; /* Color for filled stars (yellow) */
    font-size: 20px; /* Adjust the size of the stars */
    margin-right: 2px; /* Adjust the spacing between stars */
}
/* CSS for the two-column layout */
.skills-row {
    display: flex;
    justify-content: space-between;
}

.skills-column {
    flex: 1;
    padding: 10px;
    margin-right: 10px; /* Add margin between columns */
}

.skill-item {
    margin-bottom: 10px;
}
