css:
.main{
    margin:0;
    padding:0;

}
.header{
    text-align:center;
    background-color:#ff6161;
    display: flex;
    color:white;
    height:150px;
    align-items: center;

}
h3 {
    background-color:#f3f2f2;
    color:#ff6161;
    padding:3px;
}
.header img{
        width: 150px; 
        height: 150px;
        overflow: hidden;
    }

h1 {
        margin: auto;
        font-size:45px;
        font-weight:bold;
    }
.left{
        float: left;
        width: 30%; 
    }
.right{
        float: left;
        width: 70%; 
    }
div li {
        padding-left:10px;
    }
.print-format th {
        color:black;
        font-weight:bold;
    }
p {
        font-size:13px;
        padding-left:10px;
    }
td{
        font-size:13px;
    }
li{
    margin-left:15px;
}

.td{
    text-align:center;
}

/* Define the styles for the rating */
.rating {
    width: 200px;
    height: 8px;
    position: relative;
    margin-top: 5px;
}

.rating-bar {
    width: 100%;
    height: 100%;
    background-color: #f3f2f2;
}

.rating-value {
    height: 100%;
    background-color: lightblue;
}
