css:
.container{
    margin:0;
    padding:0;
    width:100%;
    font-size:14px;
    height: 100%;
    display: flex;
}
img {
    width:150px;
    height:150px;
    border-radius:50%;
}
.image{
    display: block;
    margin: 0 auto;
}
.print-format th {
        color:black;
        font-weight:bold;
    }
.left{
    flex: 1;
    padding-left:5px;
    min-height: 100%; /* Ensure left div fills at least the height of the container */

}
.right{
    flex: 2;
    padding-left:5px;
}
.heading2 h3{
    margin-top:10px;
    margin-bottom: 0;
}
.contact{
    padding-top:10px;
}
.header h1{
    font-size:50px;
    font-weight:bold;
    text-align:center;
}
div ul li {
    padding: 0;
}
table {
    padding-left: 0;
}
p{
    padding: 2px 5px;
}

/* Define the styles for the rating */
.rating {
    width: 180px;
    height: 5px;
    position: relative;
    margin-top: 5px;
    margin-left: 5px;
}

.rating-bar {
    width: 100%;
    height: 100%;
    background-color: #f3f2f2;
}

.rating-value {
    height: 100%;
    background-color: darkblue;
}
